import React, { useEffect, useState } from "react";
import api from "../api";
import ProjectCard from "./ProjectCard";

const staticProjects = [
  { img: "/images/currency converter.jpg", link: "https://amruthakalyan.github.io/currency-converter_react/" },
  { img: "/images/imageSearch.png", link: "https://amruthakalyan.github.io/imagesearch_react/" },
  { img: "/images/generator-jokes.jpg", link: "https://amruthakalyan.github.io/JokesGenerator_react/" },
  { img: "/images/download.jpg", link: "https://amruthakalyan.github.io/hotstar-clone/" },
  { img: "/images/tictactoeimg.jpg", link: "https://amruthakalyan.github.io/tictactoe/" },
  { img: "/images/tkrlogo.jpg", link: "https://amruthakalyan.github.io/webclone/" },
  { img: "/images/calculatorimg.jpg", link: "https://amruthakalyan.github.io/calculator/" },
  { img: "/images/quizimg.jpg", link: "https://amruthakalyan.github.io/Quiz/" },
  { img: "/images/randomnoimg.jpg", link: "https://amruthakalyan.github.io/RandomNumberGenerator/" },
  { img: "/images/portfolioimg.jpg", link: "https://amruthakalyan.github.io/personal-portfolio/" },
  { img: "/images/textutilsimg.jpg", link: "https://amruthakalyan.github.io/word-counter/" },
  { img: "/images/weatherimg.png", link: "https://amruthakalyan.github.io/weather-App/" },
  { img: "/images/alarmimg.jpg", link: "https://amruthakalyan.github.io/alarm/" },
  { img: "/images/Age-calculator.jpg", link: "https://amruthakalyan.github.io/Age-calculator" },
  { img: "/images/typingTest.png", link: "https://amruthakalyan.github.io/TypingSpeed-Test/" },
  { img: "/images/snake.jpeg", link: "https://amruthakalyan.github.io/SnakeGame/" },
  { img: "/images/carPrice.jpg", link: "https://github.com/amruthakalyan/Used_Car_Price_Prediction_Project-DecisionTreeRegressor-" },
  { img: "/images/housePricePred.jpg", link: "https://github.com/amruthakalyan/Boston_House_Price_Prediction" },
  { img: "/images/iris_flower.jpg", link: "https://github.com/amruthakalyan/Iris_dataset" },
  { img: "/images/randomForest.jpg", link: "https://github.com/amruthakalyan/Random_Forest" },
  { img: "/images/titanicSurvival.jpg", link: "https://github.com/amruthakalyan/Titanic_Survival_Project" },
  { img: "/images/usedcarprice.jpg", link: "https://github.com/amruthakalyan/Used_Car_Price_Prediction_Project_RandomForest_Regressor" },
];

export default function Projects() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api
      .get("/projects")
      .then((r) => {
        if (r.data && r.data.length > 0) {
          setProjects(r.data);
        } else {
          setProjects(staticProjects);
        }
        setLoading(false);
      })
      .catch(() => {
        setProjects(staticProjects); // fallback if API fails
        setLoading(false);
      });
  }, []);

  return (
    <section id="projects" className="portfolio">
      <h1>Projects</h1>
      <h5>Click the image to view the Page</h5>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="projects-grid">
          {projects.map((p, i) =>
            p._id ? (
              <ProjectCard key={p._id} p={p} />
            ) : (
              <a key={i} href={p.link} target="_blank" rel="noreferrer">
                <img src={p.img} alt="project" />
              </a>
            )
          )}
        </div>
      )}
    </section>
  );
}
