import React from "react";

export default function ProjectCard({ title, description, demo, repo }) {
  return (
    <div className="project-card">
      <h3>{title}</h3>
      <p>{description}</p>
      <div className="project-links">
        <a href={demo} target="_blank" rel="noreferrer" className="btn">
          üåêLive Demo
        </a>
        <a href={repo} target="_blank" rel="noreferrer" className="btn">
          GitHub Repo
        </a>
      </div>
    </div>
  );
}
